const drawing=document.getElementById("container__drawing"),colors=["#e74c3c","#8e44ad","#3498db","#e67e22","#2ecc71"],squares=500;let isPressed={left:!1,right:!1};for(let e=0;e<500;e++){const e=document.createElement("div");e.classList.add("square"),drawing.addEventListener("mousedown",(e=>{e.preventDefault(),0==e.button?isPressed.left=!0:2==e.button&&(isPressed.right=!0)})),e.addEventListener("mouseover",(()=>{setColor(e,isPressed),removeColor(e,isPressed)})),e.addEventListener("mousedown",(t=>{0==t.button?(isPressed.left=!0,setColor(e,isPressed)):2==t.button&&(isPressed.right=!0,removeColor(e,isPressed))})),drawing.addEventListener("mouseup",(e=>{e.preventDefault(),isPressed.left=!1,isPressed.right=!1})),drawing.addEventListener("contextmenu",(e=>{e.preventDefault()})),drawing.appendChild(e)}function setColor(e,t){if(!0===t.left){const t=getRandomColor();e.style.background=t,e.style.boxShadow=`0 0 2px ${t}, 0 0 10px ${t}`}}function removeColor(e,t){!0===t.right&&(e.style.background="#1d1d1d",e.style.boxShadow="0 0 2px #000")}function getRandomColor(){return colors[Math.floor(Math.random()*colors.length)]}